let currentIndex = 0; const images = document.querySelectorAll(".image-container"), dots = document.querySelectorAll(".dot"); function showSlide(e) { images.forEach((t, n) => { t.style.display = n === e ? "block" : "none" }), dots.forEach((t, n) => { t.classList.toggle("active", n === e) }) } function nextSlide() { showSlide(currentIndex = (currentIndex + 1) % images.length) } setInterval(nextSlide, 3e3);